const dpi = 600;
export class App {
  constructor() {
    console.clear();
    this.canvas = document.getElementById("canvas");
    this.ctx = canvas.getContext("2d");
  }
  async createImage(src) {
    return new Promise((resolve, err) => {
      const img = new Image();
      img.src = src;
      img.onload = () => {
        resolve(img);
      };
    });
  }

  async createOutlineImage(img) {
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");

    let lineWidth = 3;
    const width = img.width + lineWidth - 1;
    const height = img.height + lineWidth - 1;
    canvas.width = width;
    canvas.height = height;
    for (let x = 0; x < lineWidth; x++) {
      for (let y = 0; y < lineWidth; y++) {
        ctx.drawImage(img, x, y);
      }
    }
    const imgData = ctx.getImageData(0, 0, width, height);
    const pix = imgData.data;
    const pixLen = pix.length;
    for (let pixel = 0; pixel < pixLen; pixel += 4) {
      pix[pixel + 2] = 255;
      pix[pixel + 1] = 255;
      pix[pixel] = 255;
    }
    ctx.putImageData(imgData, 0, 0);
    return this.createImage(canvas.toDataURL());
  }

  async loadPieces() {
    return new Promise((resolve, reject) => {
      // rnbqkpRNBQKP
      const data = [
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
      ];

      let imagePromises = data.map((d) => this.createImage(d));

      Promise.all(imagePromises).then((images) => {
        let outlinePromises = images.map((img) => this.createOutlineImage(img));
        Promise.all(outlinePromises).then((outlines) => {
          resolve({
            r: [images[0], outlines[0]],
            n: [images[1], outlines[1]],
            b: [images[2], outlines[2]],
            q: [images[3], outlines[3]],
            k: [images[4], outlines[4]],
            p: [images[5], outlines[5]],
            R: [images[6], outlines[6]],
            N: [images[7], outlines[7]],
            B: [images[8], outlines[8]],
            Q: [images[9], outlines[9]],
            K: [images[10], outlines[10]],
            P: [images[11], outlines[11]],
          });
        });
      });
    });
  }

  drawChessPiece(piece, x, y) {
    const ctx = this.ctx;
    ctx.save();
    ctx.translate(5 + x * 10, 5 + y * 10);
    const drawImage = (img, s) => {
      ctx.save();
      ctx.scale(s, s);
      ctx.translate(-img.width / 2, -img.height / 2);
      ctx.drawImage(img, 0, 0);
      ctx.restore();
    };
    drawImage(this.pieces[piece][1], 0.22);
    drawImage(this.pieces[piece][0], 0.22);
    ctx.restore();
  }

  drawBoard(board) {
    const isWhite = board.activeColor === "w";
    const ctx = this.ctx;
    ctx.beginPath();
    ctx.lineWidth = 0.25;
    const lines = 10 * 6;
    for (let i = 0; i < lines; i++) {
      ctx.moveTo(0, i * 80 / lines);
      ctx.lineTo(i * 80 / lines, 0);
      ctx.moveTo(80, i * 80 / lines);
      ctx.lineTo(i * 80 / lines, 80);
    }
    ctx.stroke();
    for (let i = 0; i < 8; i++) {
      ctx.textBaseline = "middle";
      ctx.textAlign = "center";
      ctx.font = "4px arial";
      ctx.fillStyle = "black";
      ctx.fillText(isWhite ? 8 - i : i + 1, -3, 5 + i * 10);
      ctx.fillText(
        String.fromCharCode(isWhite ? 97 + i : 97 + 7 - i),
        i * 10 + 5,
        80 + 4
      );
      for (let j = 0; j < 8; j++) {
        if ((i + j) % 2 == 0) {
          ctx.fillStyle = "white";
          ctx.fillRect(i * 10, j * 10, 10, 10);
        }
      }
    }

    ctx.lineWidth = 0.4;
    ctx.beginPath();
    ctx.rect(0, 0, 80, 80);
    ctx.stroke();
    ctx.beginPath();
    ctx.fillStyle = isWhite ? "white" : "black";
    ctx.fillRect(83, 74, 3, 3);
    ctx.rect(83, 74, 3, 3);
    ctx.stroke();

    ctx.beginPath();
    ctx.save();
    ctx.translate(84.5, 67);
    ctx.moveTo(0, 5);
    ctx.lineJoin = "miter";
    ctx.lineTo(0, 0);
    ctx.moveTo(-1, 2);
    ctx.lineTo(0, 0);
    ctx.lineTo(1, 2);
    ctx.stroke();
    ctx.restore();
  }

  drawPieces(board) {
    const ctx = this.ctx;
    ctx.beginPath();

    const placement =
      board.activeColor === "w"
        ? board.placement
        : board.placement.split("").reverse().join("");
    for (let i = 0; i < placement.length; i++) {
      if (placement[i] == " ") continue;
      let x = i % 8;
      let y = Math.floor(i / 8);
      this.drawChessPiece(placement[i], x, y);
    }
  }

  fen2Board(fen) {
    const e = fen.split(" ");
    return {
      placement: e[0]
        .replaceAll("1", " ")
        .replaceAll("2", "  ")
        .replaceAll("3", "   ")
        .replaceAll("4", "    ")
        .replaceAll("5", "     ")
        .replaceAll("6", "      ")
        .replaceAll("7", "       ")
        .replaceAll("8", "        ")
        .replaceAll("/", ""),
      activeColor: e[1],
      castling: e[2],
      enPassant: e[3],
      halfMove: e[4],
      fullMove: e[5],
    };
  }

  drawDiagram(fen) {
    const ctx = this.ctx;
    ctx.scale(dpi / 40, dpi / 40);
    ctx.translate(-40, -40);
    const board = this.fen2Board(fen);
    this.drawBoard(board);
    this.drawPieces(board);
  }

  async load() {
    this.pieces = await this.loadPieces();
  }

  drawText(size, text, x, y) {
    const ctx = this.ctx;
    ctx.textBaseline = "middle";
    ctx.textAlign = "center";
    ctx.font = size * 5 * dpi / 300 + "px arial";
    ctx.fillStyle = "black";
    ctx.fillText(text, x, y);
  }

  getModelGameText(pgn) {
    const e = pgn
      .replace(/(?:\r\n|\r|\n)/g, " ")
      .split(/[\]\[]/)
      .map((x) => x.trim())
      .filter((x) => x.length > 0);
    const lines = [];
    for (var i = 0; i < e.length; i++) {

      lines.push("");
      let line = "";
      let x = [];
      let game = e[i].split(" ");
      for (var j = 0; j < game.length; j++) {
        let ch = game[j][0];
        if (ch >= "0" && ch <= "9") {
          if (line.length > 0) {
            x.push(line.trim());
            line = "";
          }
        }
        line = line + " " + game[j];
      }
      x.push(line.trim());
      line = "";
      for (let i = 0; i < x.length; i++) {
        if (line.length + x[i].length > 45) {
          lines.push(line.trim());
          line = "";
        }
        line = line + " " + x[i];
      }
      lines.push(line.trim());
    }

    return lines;
  }

  downloadImage(img, name) {
    const link = document.createElement("a");
    link.href = img.src;
    link.download = name;
    link.click();
  }

  async generateCard(card) {
    const ctx = this.ctx;
    canvas.height = 3.5 * dpi;
    canvas.width = 2.5 * dpi * 2;
    ctx.fillStyle = "#fff";
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    ctx.save();
    ctx.translate(canvas.width / 4, canvas.height * 0.42);
    const fen = card.fen;
    this.drawDiagram(fen);
    ctx.restore();

    let lines;
    this.drawText(10, card.opening, canvas.width / 4, canvas.height * 0.06);
    this.drawText(8, card.variation, canvas.width / 4, canvas.height * 0.82);
    lines = this.getModelGameText(card.variationLine);
    for (var i = 0; i < lines.length; i++) {
      this.drawText(6, lines[i], canvas.width / 4, canvas.height * .84 + dpi / 6 * i);
    }
    this.drawText(8, card.gameTitle, (3 * canvas.width) / 4, canvas.height * 0.06);
    lines = this.getModelGameText(card.modelGame);
    for (var i = 0; i < lines.length; i++) {
      this.drawText(6, lines[i], (3 * canvas.width) / 4, canvas.height * .10 + dpi / 6 * i);
    }

    // Dashed line
    ctx.beginPath();
    ctx.strokeStyle = "black";
    ctx.lineWidth = 3;
    ctx.setLineDash([dpi * .01, dpi * 0.02]);
    ctx.moveTo(canvas.width / 2, 0);
    ctx.lineTo(canvas.width / 2, canvas.height);
    ctx.stroke();
    ctx.rect(1, 1, canvas.width - 1, canvas.height - 1);
    ctx.stroke();

    return this.createImage(canvas.toDataURL());
  }

  generateA4(img1, img2, img3, img4) {
    const width = 11.7 * dpi;
    const height = 8.3 * dpi;
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");
    canvas.width = width;
    canvas.height = height;
    ctx.drawImage(img1, 0, 0);
    ctx.drawImage(img2, img1.width, 0);
    ctx.drawImage(img3, 0, img1.height);
    ctx.drawImage(img4, img1.width, img1.height);
    return this.createImage(canvas.toDataURL());
  }
}
